<template>
  <div :align="align">
    <img :class="{'g-img': true, 'error': showError }" v-bind="binds" :src="imgUrl" @error="handleImgError">
  </div>
</template>

<script>
export default {
  name: 'Img',
  props: {
    src: String,
    name: String,
    h: {
      type: String,
      default: "250",
    },
    w: String,
    align: {
      type: String,
      default: 'center'
    }
  },
  data () {
    return {
      showError: false,
    }
  },
  computed: {
    binds () {
      const ret = {}
      if (this.h) ret.height = this.h
      if (this.w) ret.width = this.w
      return ret
    },
    imgUrl () {
      return this.src ? this.src : 'https://gcore.jsdelivr.net/gh/GuoLiBin6/images/tech-blog/' + this.name
    }
  },
  methods: {
    handleImgError (error) {
      this.showError = true
    }
  }
}
</script>

<style scoped>
.g-img.error {
  display: inline-block;
  transform: scale(1);
  content: '';
  color: transparent;
  width: 150px;
  height: 150px;
}
.g-img.error::before {
  content: '';
  position: absolute;
  left: 0; top: 0;
  width: 100%; height: 100%;
  background: #f5f5f5 url(data:image/png;base64,) no-repeat center / 50% 50%;
}
.g-img.error::after {
  content: attr(alt);
  position: absolute;
  left: 0; bottom: 0;
  width: 100%;
  line-height: 2;
  background-color: rgba(0,0,0,.5);
  color: white;
  font-size: 12px;
  text-align: center;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}
</style>
